def sort_data(ohm,power,wind,sort_list,sort_type="power"):

    sort_data_dict = {}



    if sort_type == "power":
        for sort_i in range(len(sort_list)):
            max_s = sort_list[sort_i]
            if sort_i > 0:
                min_s = sort_list[sort_i-1]
            else:
                min_s = 0

            for i,val in enumerate(power):
                if min_s < val <= max_s:
                    sort_data_dict.setdefault(max_s,{"power":[],"ohm":[],"wind":[]})["power"].append(val)
                    sort_data_dict[max_s]["ohm"].append(ohm[i])
                    sort_data_dict[max_s]["wind"].append(wind[i])

        for i,val in enumerate(power):

            if val > sort_list[-1]:
                sort_data_dict.setdefault(str(sort_list[-1])+"+", {"power": [], "ohm": [], "wind": []})["power"].append(val)
                sort_data_dict[str(sort_list[-1])+"+"]["ohm"].append(ohm[i])
                sort_data_dict[str(sort_list[-1])+"+"]["wind"].append(wind[i])

    return sort_data_dict


